mrc.controller('AppCtrl', ["$scope", "$ionicModal", "$timeout", "$state", "$auth", "$rootScope", function($scope, $ionicModal, $timeout, $state, $auth, $rootScope) {

  // With the new view caching in Ionic, Controllers are only called
  // when they are recreated or on app start, instead of every page change.
  // To listen for when this page is active (for example, to refresh data),
  // listen for the $ionicView.enter event:
  //$scope.$on('$ionicView.enter', function(e) {
  //});

  // Form data for the login modals
  $scope.loginData = {};

  // Create the login modal that we will use later
  $ionicModal.fromTemplateUrl('templates/login.html', {
    scope: $scope
  }).then(function(modal) {
    $scope.modal = modal;
  });

  // Triggered in the login modal to close it
  $scope.closeLogin = function() {
    $scope.modal.hide();
  };

  // Open the login modal
  $scope.login = function() {
    $scope.modal.show();
  };

  // Perform the login action when the user submits the login form
  $scope.doLogin = function(provider) {
    console.log('Doing login', $scope.loginData);

    // Simulate a login delay. Remove this and replace with your login
    // code if using a login system
    /*$timeout(function() {
      $scope.closeLogin();
    }, 1000);*/
    // $auth.authenticate(provider);
    $auth.login($scope.loginData, {url: $rootScope.app.apiUrl + '/api/auth/authenticate', method: 'POST'})
    .then(function(response) {
      //$state.go('app.home');
    })
    .catch(function(response) {
      // Handle errors here, such as displaying a notification
      // for invalid email and/or password.
    });

  };

  $scope.changeLanguage = function (langKey) {
    $translate.use(langKey);
  };

  $scope.isActive = function(stateName){
    return $state.includes(stateName);
  };

}]);

